import React , {Component} from "react";
import "./estilo.css";

class FormularioCadastro extends Component {
    
    //declarar atributos da classe
        constructor(props){
            super(props);
            //declaracao e atribuicao
            this.titulo = "";
            this.texto = "";
            this.categoria = "Sem categoria";
            this.state = {categorias:[]}
            this._novasCategorias = this._novasCategorias.bind(this);
        }
        componentDidMount(){
            this.props.categorias.inscrever(this._novasCategorias.bind(this));
            this.props.categorias.desinscrever(this._novasCategorias.bind(this));

        }
        //ira ser chamado toda vez que o component for destruido(desinscrever)
        componentWillUnmount(){

        }
        _novasCategorias(categorias){
            this.setState({...this.state, categorias})
          }
    //metodo que gerencia a funcao de mudanca de titulo
    //Aqui o metodo ira receber por parametro o evento
        _handleMudancaTitulo(evento){
            //cada vez que o metodo handler for chamado 
            //ele ira atribuir ao campo titulo 
            //atribuicao
            evento.stopPropagation();
            this.titulo = evento.target.value;

        }
        _handleMudancaCategoria(evento){
            evento.stopPropagation();
            this.categoria = evento.target.value;
        }

        _handleMudancaTexto(evento) {
            evento.stopPropagation();
            this.texto = evento.target.value;
          }
        

          _criarNota(evento) {
            evento.preventDefault();
            evento.stopPropagation();
            this.props.criarNota(this.titulo, this.texto, this.categoria);
          }
        
        render() {
            return (
              <form className="form-cadastro" onSubmit={this._criarNota.bind(this)}>
                <select
                  onChange={this._handleMudancaCategoria.bind(this)}
                  className="form-cadastro_input"
                >
                  <option>Sem Categoria</option>
        
                  {this.state.categorias.map((categoria, index) => {
                    return <option key={index} >{categoria}</option>;
                  })}
                </select>
                <input
                  type="text"
                  placeholder="TÃ­tulo"
                  className="form-cadastro_input"
                  onChange={this._handleMudancaTitulo.bind(this)}
                />
                <textarea
                  rows={15}
                  placeholder="Escreva sua nota..."
                  className="form-cadastro_input"
                  onChange={this._handleMudancaTexto.bind(this)}
                />
                <button className="form-cadastro_input form-cadastro_submit">
                  Criar Nota
                </button>
              </form>
            );
          }
        }
        
        export default FormularioCadastro;
